sys_variable_value.value:
(function execute(inputs, outputs) {
       var varname = inputs.variablename.toString();
       var varvalue = inputs.variablevalue.toString();
       var catitem = inputs.catalogitem.toString();

       var ritm = "";
       var gr = new GlideRecord('sc_item_option_mtom');
       gr.addQuery('sc_item_option.item_option_new.name', varname);
       gr.addQuery('sc_item_option.value', varvalue);
       gr.addQuery('request_item.cat_item.sys_id', catitem);
       gr.orderByDesc('sys_created_on');
       gr.query();

       if (gr.next()) {
              ritm = gr.request_item.sys_id.toString();
       }
       outputs.ritm = ritm;

})(inputs, outputs);

